mapper.select:
select Customer_ID ,Cert_ID ,UserName ,UserName_Bak ,Password ,Role ,TEL1 ,TEL2 ,Home_Address ,Common_Address ,Work_Type ,Work_Unit ,Remark ,Stat ,Load_Date from customer where
Customer_ID = #{Customer_ID}
TEL1=#{TEL1} and Password=#{Password}

mapper.update:
        	update customer
       		set         			
            	Cert_ID = ${Cert_ID}
            	,UserName = ${UserName}
            	,UserName_Bak = ${UserName_Bak}
            	,Password = ${Password}
            	,Role = ${Role}
            	,TEL1 = ${TEL1}
            	,TEL2 = ${TEL2}
            	,Home_Address = ${Home_Address}
            	,Common_Address = ${Common_Address}
            	,Work_Type = ${Work_Type}
            	,Work_Unit = ${Work_Unit}
            	,Remark = ${Remark}
            	,Stat = ${Stat}
            	,Load_Date = ${Load_Date}
	          where Customer_ID = ${Customer_ID}
