mapper.update:
        	update customer
       		set         			
            	cert_ID = ${Cert_ID}
            	,userName = ${userName}
            	,custName = ${custName}
            	,password = ${password}
            	,role = ${role}
            	,tel = ${tel}
            	,home_Address = ${home_Address}
            	,remark = ${remark}
            	,work_Type = ${work_Type}
            	,stat = ${stat}
            	,load_Date = ${load_Date}
	          where customer_ID = ${customer_ID}
